    constructor(p: p5, freq: number, boundary: p5.Vector) {
        this.p = p;
        this.boundary = boundary;
        for (let i = 0; i < this.data.length; i += 3) {
            this.vBlocks.push(new VerletNode(p, new p5.Vector(this.data[i] - this.p.width / 2, this.data[i + 1] - this.p.height / 2, this.data[i + 2]), this.p.width / (freq * this.p.random(1.5, 2.5)), p.color(this.cols[i] + this.p.int(this.p.random(-15, 15)), this.cols[i + 1] + this.p.int(this.p.random(-15, 15)), this.cols[i + 2] + this.p.int(this.p.random(-15, 15)))));
        }

        for (let i = 0, k = 0; i < this.vBlocks.length; i++) {
            for (let j = i; j < this.vBlocks.length; j++) {
                if (k++ % this.p.int(this.p.random(1, 4)) == 0 && i !== j) {
                    this.vSticks.push(new VerletStick(this.p, this.vBlocks[i], this.vBlocks[j], this.p.random(.0001, .004)));
                }
            }
        }

        this.nudge();
    }

    draw() {
        for (let i = 0; i < this.vBlocks.length; i++) {
            this.vBlocks[i].verlet(); this.vBlocks[i].draw();
            this.vBlocks[i].boundsCollide(this.boundary);
        }

        for (let i = 0; i < this.vSticks.length; i++) {
            this.vSticks[i].constrainLen();
            // this.vSticks[i].draw();
        }

        if (this.p.frameCount % 1 === 0) {
            this.vBlocks[this.p.int(this.p.random(this.vBlocks.length))].nudge(new p5.Vector(this.p.random(-10, 10), this.p.random(-10, 10), this.p.random(-10, 10)));
        }
    }

    nudge(): void {
        for (let i = 0; i < this.vBlocks.length; i++) {
            this.vBlocks[i].nudge(new p5.Vector(this.p.random(-100, 100), this.p.random(-100, 100), this.p.random(-100, 100)));
        }
    }

